def load_octoporst_sign_test():
    s = ""
    octopus_no_zeros = ""
    octopus_zeros = ""
    octolen = 296
    res = OctoporstSign.load(bytes_to_int_list(bytes.fromhex(s + octopus_no_zeros)))
    res2 = OctoporstSign.load(bytes_to_int_list(bytes.fromhex(s + octopus_zeros)), 10_368)
    if list_of_hashes_to_bytes(res.s.s).hex() != s or \
            list_of_hashes_to_bytes(res.octopus).hex() != octopus_zeros or \
            res.octolen != octolen or \
            res != res2:
        print(res, res2)
        raise Exception("Test failed")


